// A reusable Card component that accepts props
function Card({ imgSrc, title, price }) {
  return (
    <div className="mcard">
      <img src={imgSrc} alt={title} />
      <h3>{title}</h3>
      <p>Price: {price} CC</p>
    </div>
  )
}

